    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        int check=0;
        String newdescription=jTextField2.getText();
        String newamountOfguest=jTextField3.getText();
        String newamountOfbed=(String)jComboBox1.getSelectedItem();
        String newprice=jTextField4.getText();
        
        if(newdescription.equals("")|| newamountOfguest.equals("")|| newamountOfbed.equals("")|| newprice.equals("")){
            check=1;
            JOptionPane.showMessageDialog(null,"All Field Is Required.");
        }else{
            ResultSet rs=Select.getData("select *from room where roomID='"+roomID+"'");
            try
            {
                if(rs.next()){
                check=1;
                InsertUpdateDelete.setData("Update room set description='"+newdescription+"', amountOfguest='"+newamountOfguest+"', amountOfbed='"+newamountOfbed+"', price='"+newprice+"' and status='Not Booked' where roomID='"+roomID+"'", "Details Set Successfully.");
                setVisible(false);
                new EditRoom().setVisible(true);
                }
                        
            }
            catch(Exception e){
                JOptionPane.showMessageDialog(null, e);
            }
        }
    }//GEN-LAST:event_jButton1ActionPerformed

