    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        int check=0;
        roomID=jTextField1.getText();
        if(roomID.equals("")){
            check=1;
            JOptionPane.showMessageDialog(null, "Room ID Is Required");
        }else{
           ResultSet rs=Select.getData("select *from room where roomID='"+roomID+"'");
           try
           {
               if(rs.next()){
                   check=1;
                   
                   jTextField1.setEditable(false);
                   jTextField2.setText(rs.getString(2));
                   jTextField3.setText(rs.getString(3));
                   jComboBox1.setSelectedItem(rs.getString(4));
                   jTextField4.setText(rs.getString(5));
               }
           }
           catch(Exception e){
            JOptionPane.showMessageDialog(null, e);
        }
        }
        if(check==0)
            JOptionPane.showMessageDialog(null, "Incorrect Room ID");
    }//GEN-LAST:event_jButton3ActionPerformed

